package com.evergent.corejava.basicprogramming;

public class Pattern2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int k=25;
		for(int i=1;i<=5;i++)
		{
			for(int j=1;j<=5;j++)
			{
				System.out.print(k+" ");
				k--;
			}
			System.out.println();
		}
	}

}
